function validateEmail(e){return!!/^\w+([\.-]?\w+)*@\w+([\.-]?\w+)*(\.\w{2,3})+$/.test(e)}function validatePhoneNumber(e){return/^(\s*)?(\+)?([- _():=+]?\d[- _():=+]?){10,14}(\s*)?$/.test(e)}function inputReport(e,t){e.setCustomValidity(t),e.reportValidity()}export default function Form(){document.querySelectorAll(".form-label").forEach(e=>{let t=e.querySelector("textarea"),a=(t?.addEventListener("input",()=>{t.parentElement.dataset.text=t.value}),e.querySelector("input"));a?.addEventListener("input",()=>{a.classList.remove("is-error"),a.setCustomValidity("")}),a?.addEventListener("blur",()=>{var e=a.value.trim();e&&!a.classList.contains("is-error")?("email"==a.type&&!validateEmail(e)||"tel"==a.type&&!validatePhoneNumber(e))&&a.classList.add("is-error"):a.value=e}),a?.closest("form").addEventListener("submit",e=>{e.preventDefault(),"email"==a.type&&a.value.trim()&&!validateEmail(a.value)?(a.classList.add("is-error"),inputReport(a,"Please enter a valid email address.")):"tel"==a.type&&a.value.trim()&&!validatePhoneNumber(a.value)&&(a.classList.add("is-error"),inputReport(a,"Please enter a valid phone number."))})})}