export default function Sliders(){document.querySelectorAll(".gallery-slider").forEach(e=>{new Swiper(e,{loop:!0,slidesPerView:1,spaceBetween:16,centeredSlides:!0,navigation:{nextEl:".swiper-button-next",prevEl:".swiper-button-prev"},pagination:{el:".swiper-pagination",clickable:!0},autoplay:{disableOnInteraction:!0},breakpoints:{576:{slidesPerView:2,speed:700},992:{spaceBetween:32,slidesPerView:3}}})})}